import React from 'react';
import { MovieCard } from '../movie-card/movie-card';
import { MovieView } from '../movie-view/movie-view';

export class MainView extends React.Component {

  constructor() {
    super();
    this.state = {
      movies: [
        { _id: 1, Title: 'What Dreams May Come', Description: 'Based on Shakespeare\'s line on Hamlet, this drama portrays the idea of dreaming beyond the grave.', ImagePath: 'data:image/jpeg;base64,' },
        { _id: 2, Title: 'Maudie', Description: 'The difficult story of the painter Maud Lewis', ImagePath: 'data:image/jpeg;base64,' },
        { _id: 3, Title: '12 Angry Men', Description: 'The jury agree on a murder trial almost unanimously, except for one, who then turns everything around.', ImagePath: 'data:image/jpeg;base64,' }
      ],
      selectedMovie: null
    };
  }

  render() {
    const { movies, selectedMovie } = this.state;

    if (selectedMovie) return <MovieView movie={selectedMovie} />;

    if (movies.length === 0) return <div className='main-view'>The list is empty!</div>;

    return (
      <div className='main-view'>
        {movies.map(movie => <MovieCard key={movie._id} movie={movie} />)}
      </div>
    );
  }
}
